import React from 'react';
import { useState } from 'react';
import  axios  from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [data, setData] = useState({});
    const navigator = useNavigate()

    const handleSubmit = async (e) => {
        e.preventDefault();
        const result = await axios.post('http://localhost:3002/api/auth', data)
        localStorage.setItem('token', result.data)
        navigator('/comments')
    }

    return (
        <div>
            <form className="m-3" onSubmit={(e)=>handleSubmit(e)}>
            <div className="mb-3">
                <label className="form-label"> email: </label>
                <input type="email" className="form-control" id="email" placeholder="enter email" required={true} onChange={(ev)=>setData({...data,email:ev.target.email})}/>
            </div>
            <div className="mb-3">
                <label className="form-label"> password: </label>
                <input type="password" className="form-control" id="password" placeholder="enter password" required={true} onChange={(ev)=>setData({...data,password:ev.target.password})}/>
            </div>
            <button type='submit' className='btn btn-primary'>submit</button>
            </form>
        </div>
    );
}

export default Login;
