import React from 'react';
import { useState } from 'react';
import  axios  from 'axios';
import { useNavigate } from 'react-router-dom';

const Register = function(){
    const [data, setData] = useState({});
    const navigator = useNavigate()

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(data);
        const result = await axios.post('http://localhost:3002/api/users', data)
        localStorage.setItem('token', result.headers['x-auth-token'])
        navigator('/comments')
    }
    return (
        <div>
            <form className="m-3">
            <div class="mb-3" onSubmit={handleSubmit}>
                <label className="form-label"> name: </label>
                <input type="text" class="form-control" id="name" placeholder="enter name" required="true" onChange={(ev)=>setData({...data,name:ev.target.name})}/>
            </div>
            <div class="mb-3">
                <label className="form-label"> email: </label>
                <input type="email" class="form-control" id="email" placeholder="enter email" required="true" onChange={(ev)=>setData({...data,email:ev.target.email})}/>
            </div>
            <div class="mb-3">
                <label className="form-label"> password: </label>
                <input type="password" class="form-control" id="password" placeholder="enter password" required="true" onChange={(ev)=>setData({...data,password:ev.target.password})}/>
            </div>
            <button type='submit' className='btn btn-primary'>sign in</button>
            </form>
        </div>
    );
}

export default Register;
